Scripts.OnDragStart:
if ( IsControlKeyDown() ) then
this:LockHighlight();
this:StartMoving();
end

Scripts.OnMouseUp:
if ( arg1 == "LeftButton" ) then
    LFT_Toggle();
elseif ( arg1 == "RightButton" ) then
    if ( IsControlKeyDown() ) then
        this:SetUserPlaced(0);
        this:ClearAllPoints();
        this:SetParent("Minimap");
        this:SetPoint("LEFT", Minimap, -22, -14);
    end
end
