Scripts.OnClick:
if ( this:GetButtonState() == "PUSHED" ) then
this.clickDelay = MESSAGE_SCROLLBUTTON_INITIAL_DELAY;
else
PlaySound("igChatScrollDown");
this:GetParent():ScrollDown();
end

if ( this:GetButtonState() == "PUSHED" ) then
this.clickDelay = MESSAGE_SCROLLBUTTON_INITIAL_DELAY;
else
PlaySound("igChatScrollUp");
this:GetParent():ScrollUp();
end

Scripts.OnEnter:
GameTooltip:SetOwner(this, "ANCHOR_TOPRIGHT");
GameTooltip:SetText('Rank Access', 1, 1, 1, nil, 1);
GameTooltip:AddLine('Minimum Rank to access this tab.', nil, nil, nil, nil, 1);
GameTooltip:Show();

GameTooltip:SetOwner(this, "ANCHOR_TOPRIGHT");
GameTooltip:SetText('Weekly Withdrawals', 1, 1, 1, nil, 1);
GameTooltip:AddLine('Maximum stacks a member can withdraw weekly.', nil, nil, nil, nil,
1);
GameTooltip:Show();
